    –î–æ–±–∞–≤—å—Ç–µ —Å–ª–µ–¥—É—é—â–∏–π –∫–æ–¥ –≤ –∫–æ–Ω–µ—Ü —Ñ–∞–π–ª–∞ MapZoomOut.lua:
   
      local function OnUpdate(self, elapsed)
        -- –£–≤–µ–ª–∏—á–∏–≤–∞–µ–º —Å—á—ë—Ç—á–∏–∫
        counter = counter + elapsed
        
        if counter >= DELAY then
          -- –ü–æ–ª—É—á–∞–µ–º —Ç–µ–∫—É—â–∏–π —É—Ä–æ–≤–µ–Ω—å –º–∞—Å—à—Ç–∞–±–∏—Ä–æ–≤–∞–Ω–∏—è
          local z = Minimap:GetZoom()
  
          if z > 0 then
            origSetZoom(Minimap, z - 1)
          else
            -- –í–∫–ª—é—á–∞–µ–º/–≤—ã–∫–ª—é—á–∞–µ–º –∫–Ω–æ–ø–∫–∏
            MinimapZoomIn:Enable()
            MinimapZoomOut:Disable()
            self:Hide()
          end
        end
      end
    
    –í –Ω–∞—á–∞–ª–µ —Ç–∞–π–º–µ—Ä–∞ –ø—Ä–æ–∏—Å—Ö–æ–¥–∏—Ç —Ç–æ –∂–µ ‚Äî —É–≤–µ–ª–∏—á–µ–Ω–∏–µ —Å—á—ë—Ç—á–∏–∫–∞ –∏ –ø—Ä–æ–≤–µ—Ä–∫–∞ —Å –∑–∞–¥–µ—Ä–∂–∫–æ–π. –í–Ω—É—Ç—Ä–∏ —É—Å–ª–æ–≤–∏—è —Ç–µ–∫—É—â–∏–π —É—Ä–æ–≤–µ–Ω—å –º–∞—Å—à—Ç–∞–±–∏—Ä–æ–≤–∞–Ω–∏—è —Å–æ—Ö—Ä–∞–Ω—è–µ—Ç—Å—è –≤ –ª–æ–∫–∞–ª—å–Ω—É—é –ø–µ—Ä–µ–º–µ–Ω–Ω—É—é z –¥–ª—è —É–¥–æ–±—Å—Ç–≤–∞. –ï—Å–ª–∏ –º–∏–Ω–∏–∫–∞—Ä—Ç–∞ —É–≤–µ–ª–∏—á–µ–Ω–∞ (z > 0), —Ç–æ –º–∞—Å—à—Ç–∞–± —É–º–µ–Ω—å—à–∞–µ—Ç—Å—è –Ω–∞ –æ–¥–∏–Ω —É—Ä–æ–≤–µ–Ω—å, –Ω–æ —Å—á—ë—Ç—á–∏–∫ –Ω–µ —Å–±—Ä–∞—Å—ã–≤–∞–µ—Ç—Å—è. –≠—Ç–æ –≥–∞—Ä–∞–Ω—Ç–∏—Ä—É–µ—Ç, —á—Ç–æ —Ñ—É–Ω–∫—Ü–∏—è —Ç–∞–π–º–µ—Ä–∞ –≤—ã–ø–æ–ª–Ω–∏—Ç—Å—è –∏ –≤ —Å–ª–µ–¥—É—é—â–µ–º –æ–±–Ω–æ–≤–ª–µ–Ω–∏–∏ –∫–∞–¥—Ä–∞. –ò–º–µ–Ω–Ω–æ —ç—Ç–æ –æ–±–µ—Å–ø–µ—á–∏–≤–∞–µ—Ç –ø–ª–∞–≤–Ω–æ–µ —É–º–µ–Ω—å—à–µ–Ω–∏–µ –º–∞—Å—à—Ç–∞–±–∞, –∞ –Ω–µ —Ä–µ–∑–∫–∏–π —Å–∫–∞—á–æ–∫ –∑–∞ –æ–¥–∏–Ω –∫–∞–¥—Ä.
    
    –ù–∞–∫–æ–Ω–µ—Ü, –∫–æ–≥–¥–∞ –º–∏–Ω–∏–∫–∞—Ä—Ç–∞ –ø–æ–ª–Ω–æ—Å—Ç—å—é —É–º–µ–Ω—å—à–µ–Ω–∞, –∫–Ω–æ–ø–∫–∞ —É–≤–µ–ª–∏—á–µ–Ω–∏—è –º–∞—Å—à—Ç–∞–±–∞ MinimapZoomIn —Å—Ç–∞–Ω–æ–≤–∏—Ç—Å—è –∞–∫—Ç–∏–≤–Ω–æ–π, –∫–Ω–æ–ø–∫–∞ —É–º–µ–Ω—å—à–µ–Ω–∏—è MinimapZoomOut ‚Äî –Ω–µ–∞–∫—Ç–∏–≤–Ω–æ–π, –∏ —Å–∞–º —Ç–∞–π–º–µ—Ä —Å–∫—Ä—ã–≤–∞–µ—Ç—Å—è, —á—Ç–æ–±—ã –µ–≥–æ —Ñ—É–Ω–∫—Ü–∏—è OnUpdate –±–æ–ª—å—à–µ –Ω–µ –≤—ã–∑—ã–≤–∞–ª–∞—Å—å.
    
